import React, { useEffect, useState } from "react";
// import ProfileCard from "./component/ProfileCard";
import HomeCarousel from "./component/HomeCarousel";
import StafCarousel from "./component/StafCarousel";
import StudentCarousel from "./component/StudentCarousel";
import StudentList from "./component/StudentList";

const HomeAdmin = () => {
  const [adminResult, setAdminResult] = useState({})

  const changeAdminOptions = async()=>{
    try {
      const response = await fetch(
        `http://localhost:4000/aadmin/home/webPageOptions/editing`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            "auth-token": localStorage.getItem("Admin-token"),
          },
          body: JSON.stringify({
            name,
            email,
            address,
            business_organization,
            comments,
            dob,
            passing_year,
            phone,
            position,
            profession,
            website,
            profile_img,
          }),
        }
      );
      const result = await response.json();
      return result.success
    } catch (error) {
      console.log(error);
    }
  }
  const getAdminOptions = async () => {
    try {
      const response = await fetch(
        `http://localhost:4000/aadmin/home/webPageOptions/${localStorage.getItem("Admin-token")}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": localStorage.getItem("Admin-token"),
          },
        }
      );
      const result = await response.json();
      // console.log(result.result);
      return result.result
    } catch (error) {
      console.log(error);
    }
  };
  
  useEffect(() => {
    getAdminOptions().then((resolve)=>{setAdminResult(resolve)})
    console.log(adminResult.homeCarousel)
  }, [])
  getAdminOptions()
  return (
    <>
      <h2 className="w-100 m-3">hiii</h2>
      <div className="d-flex row mx-3"></div>
      <div className="d-flex row mx-3">
        <div className="card my-1">
          <HomeCarousel carousel={adminResult.homeCarousel} convertToBase64={convertToBase64}/>
        </div>
        <div className="card my-1">
          <StafCarousel />
          {/* <ProfileCard /> */}
        </div>
        <div className="card my-1">
          <StudentCarousel />
        </div>
        <div className="card my-1">{/* <StafCarousel /> */}</div>
        <div className="card my-1">
          <StudentList />
        </div>
      </div>
    </>
  );
};

export default HomeAdmin;


function convertToBase64(file){
  return new Promise((resolve,reject)=>{
    const filereader = new FileReader()
    filereader.readAsDataURL(file)
    filereader.onload=()=>{
      resolve(filereader.result)
    }
    filereader.onerror = (error)=>{reject(error)}
  })
}
