import React, { useContext, useEffect, useState } from 'react';
import StudentContext from '../contextstate/StudentContext';
// import './component styling/Carousel.js'

const Home = () => {
    const context = useContext(StudentContext)
    const {setShowHeader} = context
    setShowHeader("")
    const [homeContent, sethomeContent] = useState({})

    const getPageData = async () => {
        try {
          const response = await fetch(
            `http://localhost:4000/aadmin/home/webPageOptions`,
            {
              method: "GET",
              headers: {
                "Content-Type": "application/json",
              },
            }
          );
          const result = await response.json();
          // console.log(result.result.homeCarousel;
          return result.result[0]
        } catch (error) {
          console.log(error);
        }
      };
      useEffect(() => {
        getPageData().then((resolve)=>{
            sethomeContent({
                homeCarousel:resolve.homeCarousel,
                stafCarousel:resolve.stafData.stafCarousel,
                studentCarousel:resolve.studentCarousel,
            })
        })
        console.log(homeContent)
      }, [])
    return (<>
    
    <div className="container">
    <div id="carouselExampleInterval" class="carousel slide" data-bs-ride="carousel">
  <div class="carousel-inner">
    <div class="carousel-item active" data-bs-interval="1000">
      <img src="https://img.freepik.com/premium-photo/boats-lake-with-mountains-background_865967-184154.jpg" class="d-block w-100" alt="..."/>
    </div>
    <div class="carousel-item" data-bs-interval="1000">
      <img src="https://images.pexels.com/photos/620337/pexels-photo-620337.jpeg?cs=srgb&dl=pexels-tobi-620337.jpg&fm=jpg" class="d-block w-100" alt="..."/>
    </div>
    <div class="carousel-item" data-bs-interval="1000">
      <img src="https://img.freepik.com/premium-photo/lake-with-canoes-mountains-background_865967-298007.jpg" class="d-block w-100" alt="..."/>
    </div>
  </div>
  <button class="carousel-control-prev" type="button" data-bs-target="#carouselExampleInterval" data-bs-slide="prev">
    <span class="carousel-control-prev-icon" aria-hidden="true"></span>
    <span class="visually-hidden">Previous</span>
  </button>
  <button class="carousel-control-next" type="button" data-bs-target="#carouselExampleInterval" data-bs-slide="next">
    <span class="carousel-control-next-icon" aria-hidden="true"></span>
    <span class="visually-hidden">Next</span>
  </button>
</div>
    </div>
        
    <div>Lorem ipsum dolor sit amet consectetur adipisicing elit. Numquam voluptatibus sunt dignissimos, id eius, cumque esse temporibus excepturi alias vitae qui.</div>;
    <div className="container ">
    <img className="" src={homeContent.homeCarousel ? homeContent.homeCarousel.img_1 : "https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"} alt="" style={{height:"200px",width:"350px"}} onClick={console.log(homeContent)}/>
    </div>
    
    </>)
}

export default Home;
