import axios from 'axios';
import React, { useEffect, useState } from 'react';


const Experiment = () => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const handleInputChange = (event) => {
      setQuery(event.target.value);
  };

  const performSearch = async () => {
      if (query.trim() === '') {
          setResults([]);
          return;
      }

      try {
          const response = await axios.get(`http://localhost:4000/api/studentinfo/search?name=${query}&address=${query}&business_organization=${query}`);
          setResults([response.data]);
      } catch (error) {
          console.error('Error fetching search results:', error);
      }
  };

  useEffect(() => {
      performSearch();
      results.map((i,index)=>{
        console.log(index)
        console.log(i[index].name)

      })
  }, [query]);

  return (
      <div>
          <input 
              type="text" 
              value={query} 
              onChange={handleInputChange} 
              placeholder="Search by name"
          />
          <ul>
              {
              results.map((item, index) => (
                  <>
                  <li key={index}>{item[index].name}</li>
                  <li key={index + 1}>{item[index + 1].name}</li>
                  <li key={index + 2}>{item[index + 2].name}</li>
                  </>
              ))
              }
          </ul>
      </div>
  );
}

export default Experiment;
