import { Modal, Button } from "react-bootstrap";
import { Link, Outlet, useLocation, useNavigate } from "react-router-dom";
import StudentContext from "../contextstate/StudentContext";
// import Button from 'react-bootstrap/Button';
// import Modal from 'react-bootstrap/Modal';
import "../App.css";

const Header = () => {
  const [student, setStudent] = useState({});
  const context = useContext(StudentContext);
  const { getStudent, showHeader } = context;
  const location = useLocation();
  const navigate = useNavigate();

  // These are logout confirmation model elements
  const [showLogout, setShowLogout] = useState(false);

  const handleCloseLogout = () => setShowLogout(false);
  const handleShowLogout = () => setShowLogout(true);

  // These are SideManueBar handling components
  const [showSideBar, setshowSideBar] = useState(false)
  const handleSideBarOpen = ()=>{setshowSideBar(true)}
  const handleSideBarClose = ()=>{setshowSideBar(false)}

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("Admin-token");
    handleCloseModal();
    navigate("/");
  };

  const handleEdit = () => {
    // console.log(localStorage.getItem("token"))
    navigate(`/student/authorised/edit/${localStorage.getItem("token")}`);
    handleCloseModal();
  };

  // These functions are for profile Modal
  let [showModal, setShowModal] = useState(false);

  const handleShowModal = () => {
    getStudent(localStorage.getItem("token")).then((resolve) => {
      setStudent(resolve);
    });
    // setStudent(getStudent(localStorage.getItem("token")))
    setShowModal(true);
    console.log(student.profile_img);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };
  // useEffect(() => {
  // console.log(location);
  // eslint-disable-next-line
  // }, []);
  return (
    <>
    <div>
        <div
          class={`d-flex flex-column flex-shrink-0 p-3 bg-light ${showSideBar ? "":"d-none"}`}
          style={{ width: "280px" ,height:"100vh", position:"absolute",position:"fixed", 
          }}
        >
          <a
            class="d-flex align-items-center mb-3 mb-md-0 me-md-auto link-dark text-decoration-none"
          >
            <svg class="bi me-2" width="40" height="32"></svg>
            <span class="fs-4">Sidebar</span>
            <i class="fa-solid fa-arrow-left mx-5" style={{cursor:"pointer"}} onClick={handleSideBarClose}></i>
            <i class="fa-solid fa-arrow-left mx-5" style={{cursor:"pointer"}} onClick={handleSideBarClose}></i>
            
          </a>
          <hr />
          <ul class="nav nav-pills flex-column mb-auto">
            <li class="nav-item">
              <a href="#" class="nav-link active" aria-current="page">
                <svg class="bi me-2" width="16" height="16"></svg>
                Home
              </a>
            </li>
            <li>
              <a href="#" class="nav-link link-dark">
                <svg class="bi me-2" width="16" height="16"></svg>
                Dashboard
              </a>
            </li>
            <li>
              <a href="#" class="nav-link link-dark">
                <svg class="bi me-2" width="16" height="16"></svg>
                Orders
              </a>
            </li>
            <li>
              <a href="#" class="nav-link link-dark">
                <svg class="bi me-2" width="16" height="16"></svg>
                Products
              </a>
            </li>
            <li>
              <a href="#" class="nav-link link-dark">
                <svg class="bi me-2" width="16" height="16"></svg>
                Customers
              </a>
            </li>
          </ul>
          <hr />
          <div class="dropdown">
            <a
              href="#"
              class="d-flex align-items-center link-dark text-decoration-none dropdown-toggle"
              id="dropdownUser2"
              data-bs-toggle="dropdown"
              aria-expanded="false"
            >
              <img
                src="https://github.com/mdo.png"
                alt=""
                width="32"
                height="32"
                class="rounded-circle me-2"
              />
              <strong>mdo</strong>
            </a>
            <ul
              class="dropdown-menu text-small shadow"
              aria-labelledby="dropdownUser2"
            >
              <li>
                <a class="dropdown-item" href="#">
                  New project...
                </a>
              </li>
              <li>
                <a class="dropdown-item" href="#">
                  Settings
                </a>
              </li>
              <li>
                <a class="dropdown-item" href="#">
                  Profile
                </a>
              </li>
              <li>
                <hr class="dropdown-divider" />
              </li>
              <li>
                <a class="dropdown-item" href="#">
                  Sign out
                </a>
              </li>
            </ul>
          </div>
        </div>
      </div>



      <div className={`Header ${showHeader}`}>
        {/*             It is  Header                   */}
        <div className="container">
          <header className="border-bottom lh-1 py-3">
            <div className="row flex-nowrap justify-content-between align-items-center">
              <div className="col-4 pt-1">
                <a onClick={handleSideBarOpen}><i class="fa-solid fa-bars "></i></a>
              
              </div>
              <div className="col-4 text-center ">
                <Link className="nav-item nav-link" to="/">
                  <h1>BUMCA</h1>
                </Link>
              </div>
              <div className="col-4 d-flex justify-content-end align-items-center">
                {/* This is Search Button */}

                {/* <a className="link-secondary " href="/" -label="Search">
                                <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="none" stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" className="mx-3" role="img" viewBox="0 0 24 24"><title>Search</title><circle cx="10.5" cy="10.5" r="7.5" /><path d="M21 21l-5.2-5.2" />
                                </svg>
                            </a> */}

                {/* This is Login and logout setup */}
                <div>
                  {!localStorage.getItem("Admin-token") ? (
                    !localStorage.getItem("token") ? (
                      location.pathname === "/login" ? (
                        <Link
                          className="btn btn-sm btn-outline-secondary "
                          to="/signup"
                        >
                          Sign up
                        </Link>
                      ) : (
                        <Link
                          className="btn btn-sm btn-outline-secondary mx-2"
                          to="/login"
                        >
                          Log in
                        </Link>
                      )
                    ) : (
                      // This is the Profile Button
                      <Link>
                        <h3>
                          <i
                            className="fa-solid fa-user"
                            onClick={handleShowModal}
                          ></i>
                        </h3>
                      </Link>
                      // This is admin logout Button
                    )
                  ) : (
                    <Button
                      className="btn-sm border btn-info text-light"
                      variant="secondary"
                      onClick={handleShowLogout}
                    >
                      Log-out
                    </Button>
                  )}
                  {/* )) : <Button className="btn-sm border btn-info text-light" variant="secondary" onClick={handleLogout}>
                Log-out
              </Button>} */}
                </div>
              </div>
            </div>
          </header>
        </div>

        {/*         This is NavBar           */}
        <div className="container">
          <div className="nav-scroller py-1 mb-3 my-border">
            <nav className="nav nav-underline justify-content-between">
              <Link
                className={`nav-item nav-link ${
                  location.pathname === "/" ? "link-primary active" : ""
                }`}
                to="/"
              >
                Home
              </Link>
              <Link
                className={`nav-item nav-link ${
                  location.pathname === "/student" ? "link-primary active" : ""
                }`}
                to="/student"
              >
                Student
              </Link>
              <Link className="nav-item nav-link " to="/experiment">
                PhD
              </Link>
              <Link className="nav-item nav-link " to="/experiment">
                Faculty
              </Link>
              <Link className="nav-item nav-link " to="/experiment">
                Research
              </Link>
              <Link className="nav-item nav-link " to="/experiment">
                Technology
              </Link>
              {localStorage.getItem("Admin-token") ? (
                <Link className="nav-item nav-link " to="/aadmin/home">
                  Admin
                </Link>
              ) : (
                ""
              )}
            </nav>
          </div>
        </div>
      </div>
      {/* This is User profile showing modal */}
      <Modal
        size="xl"
        show={showModal}
        onHide={handleCloseModal}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Hello !!!</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="bg-body-tertiary me-md-3 pt-md-3 px-md-5 text-center overflow-hidden">
            <div className="row d-flex justify-content-between">
              <div className="col-md-6 card py-3 px-2 text-start rounded">
                <h6>{student.name}</h6>
              </div>
              <div className="col-md-5 card py-3 px-0 rounded">
                <img
                  src={
                    student.profile_img ||
                    "https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"
                  }
                  alt="Admin"
                  className="rounded m-1"
                  width=""
                />
              </div>
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer className="justify-content-center">
          <Button className="px-3" variant="secondary" onClick={handleEdit}>
            Edit
          </Button>
          <Button
            className="px-1"
            variant="secondary"
            onClick={handleShowLogout}
          >
            Log out
          </Button>
        </Modal.Footer>
      </Modal>

      {/* This is Logout confirmation showing modal */}
      <Modal show={showLogout}>
        <Modal.Header>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Are you sure you want to Logout.</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseLogout}>
            Candel
          </Button>
          <Button
            variant="primary"
            onClick={() => {
              handleLogout();
              handleCloseLogout();
            }}
          >
            Logout
          </Button>
        </Modal.Footer>
      </Modal>

      {/* This is side bar */}
      
      <Outlet />
    </>
  );
};

export default Header;
