import { Modal, Button } from "react-bootstrap";
import React, { useState } from 'react';


const HomeCarousel = () => {
  let [showModal, setShowModal] = useState(false);

  const handleCloseModal = () => {
    setShowModal(false);
  };
  const handleShowModal = () => {
    setShowModal(true);
  };

    return (<>
        <div className="d-flex justify-content-between">
            <h3 className='mt-4 mx-2'>Home Carousel Images</h3>
            <button type='button' className='btn btn-info m-2 mt-3' onClick={handleShowModal}>Edit</button>
        </div>
            <hr />
            <div className="row p-1" style={{marginBottom:"10px"}}>
                <img src={"https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"} alt="Admin" className="px-2 m-1 mb-1 col" style={{height:"10rem"}}/>
                <img src={"https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"} alt="Admin" className="px-2 m-1 mb-1 col" style={{height:"10rem"}}/>
                <img src={"https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"} alt="Admin" className="px-2 m-1 mb-1 col" style={{height:"10rem"}}/>
            </div>

            <Modal
        size="xl"
        show={showModal}
        onHide={handleCloseModal}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Hello !!!</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="bg-body-tertiary me-md-3 pt-md-3 px-md-5 text-center overflow-hidden">
            <div className="row d-flex justify-content-between">
              <div className="col-md-6 card py-3 px-2 text-start rounded"><h6>name</h6></div>
              <div className="col-md-5 card py-3 px-0 rounded">
              <img
                      src={"https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"}
                      alt="Admin"
                      className="rounded m-1"
                      width=""
                    />
              </div>
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer className="justify-content-center">
          <Button className="px-3" variant="secondary" onClick={()=>{}}>
            Edit
          </Button>
          <Button className="px-1" variant="secondary" onClick={()=>{}}>
            Log out
          </Button>
        </Modal.Footer>
      </Modal>
            </>);
}

export default HomeCarousel;
