import React, { useContext, useEffect, useState } from "react";
import StudentContext from "../contextstate/StudentContext";
// import './component styling/Carousel.js'

const Home = () => {
  const context = useContext(StudentContext);
  const { setShowHeader } = context;
  setShowHeader("");
  const [homeContent, sethomeContent] = useState({});

  const getPageData = async () => {
    try {
      const response = await fetch(
        `http://localhost:4000/aadmin/home/webPageOptions`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      const result = await response.json();
      // console.log(result.result.homeCarousel;
      return result.result[0];
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    getPageData().then((resolve) => {
      sethomeContent({
        homeCarousel: resolve.homeCarousel,
        stafCarousel: resolve.stafData.stafCarousel,
        studentCarousel: resolve.studentCarousel,
      });
    });
    console.log(homeContent);
  }, []);
  return (
    <>
      <div className="row m-4">
        {/* <div class="row justify-content-md-center"> */}
          <div class="col border border-primary b-2 p-3">1 of 3</div>
          <div class="col col-7 border border-primary b-2 p-3">
          <div id="carouselExampleAutoplaying" class="carousel slide" data-bs-ride="carousel">
  <div class="carousel-inner">
    <div class="carousel-item active">
      <img src={homeContent.homeCarousel
              ? homeContent.homeCarousel.img_1:""} class="d-block w-100" alt="..."/>
    </div>
    <div class="carousel-item">
      <img src={homeContent.homeCarousel
              ? homeContent.homeCarousel.img_1:""} class="d-block w-100" alt="..."/>
    </div>
    <div class="carousel-item">
      <img src={homeContent.homeCarousel
              ? homeContent.homeCarousel.img_1:""} class="d-block w-100" alt="..."/>
    </div>
  </div>
  <button class="carousel-control-prev" type="button" data-bs-target="#carouselExampleAutoplaying" data-bs-slide="prev">
    <span class="carousel-control-prev-icon" aria-hidden="true"></span>
    <span class="visually-hidden">Previous</span>
  </button>
  <button class="carousel-control-next" type="button" data-bs-target="#carouselExampleAutoplaying" data-bs-slide="next">
    <span class="carousel-control-next-icon" aria-hidden="true"></span>
    <span class="visually-hidden">Next</span>
  </button>
</div>
          </div>
        {/* </div> */}
      </div>
      <div>
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Numquam
        voluptatibus sunt dignissimos, id eius, cumque esse temporibus excepturi
        alias vitae qui.
      </div>
      ;
      <div className="container ">
        <img
          className=""
          src={
            homeContent.homeCarousel
              ? homeContent.homeCarousel.img_1
              : "https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"
          }
          alt=""
          style={{ height: "200px", width: "350px" }}
          onClick={console.log(homeContent)}
        />
      </div>
    </>
  );
};

export default Home;
