import React, { useContext, useEffect, useState } from "react";
import StudentContext from "../contextstate/StudentContext";
// import './component styling/Carousel.js'

const Home = () => {
  const context = useContext(StudentContext);
  const { setShowHeader } = context;
  setShowHeader("");
  const [homeContent, sethomeContent] = useState({});

  const getPageData = async () => {
    try {
      const response = await fetch(
        `http://localhost:4000/aadmin/home/webPageOptions`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      const result = await response.json();
      // console.log(result.result.homeCarousel;
      return result.result[0];
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    getPageData().then((resolve) => {
      sethomeContent({
        homeCarousel: resolve.homeCarousel,
        stafCarousel: resolve.stafData.stafCarousel,
        studentCarousel: resolve.studentCarousel,
      });
    });
    console.log(homeContent);
  }, []);
  return (
    <>
      <div className="row">
        {/* <div class="row justify-content-md-center"> */}
          <div class="col col-lg-2 border border-primary">1 of 3</div>
          <div class="col-md-auto border border-primary">Variable width content</div>
          <div class="col col-7 border border-primary">3 of 3</div>
        {/* </div> */}
      </div>
      <div>
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Numquam
        voluptatibus sunt dignissimos, id eius, cumque esse temporibus excepturi
        alias vitae qui.
      </div>
      ;
      <div className="container ">
        <img
          className=""
          src={
            homeContent.homeCarousel
              ? homeContent.homeCarousel.img_1
              : "https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"
          }
          alt=""
          style={{ height: "200px", width: "350px" }}
          onClick={console.log(homeContent)}
        />
      </div>
    </>
  );
};

export default Home;
