import React, { useContext, useEffect, useState } from 'react';
import StudentContext from '../contextstate/StudentContext';
import './component styling/Carousel.js'

const Home = () => {
    const context = useContext(StudentContext)
    const {setShowHeader} = context
    setShowHeader("")
    const [homeContent, sethomeContent] = useState({})

    const getPageData = async () => {
        try {
          const response = await fetch(
            `http://localhost:4000/aadmin/home/webPageOptions`,
            {
              method: "GET",
              headers: {
                "Content-Type": "application/json",
              },
            }
          );
          const result = await response.json();
          // console.log(result.result.homeCarousel;
          return result.result[0]
        } catch (error) {
          console.log(error);
        }
      };
      useEffect(() => {
        getPageData().then((resolve)=>{
            sethomeContent({
                homeCarousel:resolve.homeCarousel,
                stafCarousel:resolve.stafData.stafCarousel,
                studentCarousel:resolve.studentCarousel,
            })
        })
        console.log(homeContent)
      }, [])
    return (<>
    <div className="d-flex">
        
<div class="carousel" data-carousel="1" data-speed="2000">
  <span class="carousel-control-left"></span>
  <span class="carousel-control-right"></span>
  <div class="carousel-content">
    <img src="https://images.pexels.com/photos/1141853/pexels-photo-1141853.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260" alt="image 5" />
    <img src="https://images.pexels.com/photos/1714208/pexels-photo-1714208.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260" alt="image 1" />
    <img src="https://images.pexels.com/photos/1874613/pexels-photo-1874613.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260" alt="image 2" />

    <img src=" https://images.pexels.com/photos/1878715/pexels-photo-1878715.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260" alt="image 3" />

    <img src="https://images.pexels.com/photos/257360/pexels-photo-257360.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260" alt="image 4" />

    <img src="https://images.pexels.com/photos/1141853/pexels-photo-1141853.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260" alt="image 5" />
    <img src="https://images.pexels.com/photos/1714208/pexels-photo-1714208.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260" alt="image 1" />
  </div>
</div>
    </div>
    <div>Lorem ipsum dolor sit amet consectetur adipisicing elit. Numquam voluptatibus sunt dignissimos, id eius, cumque esse temporibus excepturi alias vitae qui.</div>;
    <div className="container ">
    <img className="" src={homeContent.homeCarousel ? homeContent.homeCarousel.img_1 : "https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"} alt="" style={{height:"200px",width:"350px"}} onClick={console.log(homeContent)}/>
    </div>
    
    </>)
}

export default Home;
