import React, { useContext, useState } from "react";
import { Modal, Button } from "react-bootstrap";
import { Link, Outlet, useLocation, useNavigate } from "react-router-dom";
import StudentContext from "../contextstate/StudentContext";
// import Button from 'react-bootstrap/Button';
// import Modal from 'react-bootstrap/Modal';
import "../App.css";

const Header = () => {
  const [student, setStudent] = useState({});
  const context = useContext(StudentContext);
  const { getStudent, showHeader } = context;
  const location = useLocation();
  const navigate = useNavigate();

  const [showLogout, setShowLogout] = useState(false);

  const handleCloseLogout = () => setShowLogout(false);
  const handleShowLogout = () => setShowLogout(true);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("Admin-token");
    handleCloseModal();
    navigate("/");
  };

  const handleEdit = () => {
    // console.log(localStorage.getItem("token"))
    navigate(`/student/authorised/edit/${localStorage.getItem("token")}`);
    handleCloseModal();
  };

  // These functions are for profile Modal
  let [showModal, setShowModal] = useState(false);

  const handleShowModal = () => {
    getStudent(localStorage.getItem("token")).then((resolve) => {
      setStudent(resolve);
    });
    // setStudent(getStudent(localStorage.getItem("token")))
    setShowModal(true);
    console.log(student.profile_img);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };
  // useEffect(() => {
  // console.log(location);
  // eslint-disable-next-line
  // }, []);
  return (
    <>
      <div className={`Header ${showHeader}`}>
        {/*             It is  Header                   */}
        <div className="container">
          <header className="border-bottom lh-1 py-3">
            <div className="row flex-nowrap justify-content-between align-items-center">
              <div className="col-4 pt-1"></div>
              <div className="col-4 text-center ">
                <Link className="nav-item nav-link" to="/">
                  <h1>BUMCA</h1>
                </Link>
              </div>
              <div className="col-4 d-flex justify-content-end align-items-center">
                {/* This is Search Button */}

                {/* <a className="link-secondary " href="/" -label="Search">
                                <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="none" stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" className="mx-3" role="img" viewBox="0 0 24 24"><title>Search</title><circle cx="10.5" cy="10.5" r="7.5" /><path d="M21 21l-5.2-5.2" />
                                </svg>
                            </a> */}

                {/* This is Login and logout setup */}
                <div>
                  {!localStorage.getItem("Admin-token") ? (
                    !localStorage.getItem("token") ? (
                      location.pathname === "/login" ? (
                        <Link
                          className="btn btn-sm btn-outline-secondary "
                          to="/signup"
                        >
                          Sign up
                        </Link>
                      ) : (
                        <Link
                          className="btn btn-sm btn-outline-secondary mx-2"
                          to="/login"
                        >
                          Log in
                        </Link>
                      )
                    ) : (
                      // This is the Profile Button
                      <Link>
                        <h3>
                          <i
                            className="fa-solid fa-user"
                            onClick={handleShowModal}
                          ></i>
                        </h3>
                      </Link>
                      // This is admin logout Button
                    )
                  ) : (
                    <Button
                      className="btn-sm border btn-info text-light"
                      variant="secondary"
                      onClick={handleShowLogout}
                    >
                      Log-out
                    </Button>
                  )}
                  {/* )) : <Button className="btn-sm border btn-info text-light" variant="secondary" onClick={handleLogout}>
                Log-out
              </Button>} */}
                </div>
              </div>
            </div>
          </header>
        </div>

        {/*         This is NavBar           */}
        <div className="container">
          <div className="nav-scroller py-1 mb-3 my-border">
            <nav className="nav nav-underline justify-content-between">
              <Link
                className={`nav-item nav-link ${
                  location.pathname === "/" ? "link-primary active" : ""
                }`}
                to="/"
              >
                Home
              </Link>
              <Link
                className={`nav-item nav-link ${
                  location.pathname === "/student" ? "link-primary active" : ""
                }`}
                to="/student"
              >
                Student
              </Link>
              <Link className="nav-item nav-link " to="/experiment">
                PhD
              </Link>
              <Link className="nav-item nav-link " to="/experiment">
                Faculty
              </Link>
              <Link className="nav-item nav-link " to="/experiment">
                Research
              </Link>
              <Link className="nav-item nav-link " to="/experiment">
                Technology
              </Link>
              {localStorage.getItem("Admin-token") ? (
                <Link className="nav-item nav-link " to="/aadmin/home">
                  Admin
                </Link>
              ) : (
                ""
              )}
            </nav>
          </div>
        </div>
      </div>
      {/* This is User profile showing modal */}
      <Modal
        size="xl"
        show={showModal}
        onHide={handleCloseModal}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Hello !!!</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="bg-body-tertiary me-md-3 pt-md-3 px-md-5 text-center overflow-hidden">
            <div className="row d-flex justify-content-between">
              <div className="col-md-6 card py-3 px-2 text-start rounded">
                <h6>{student.name}</h6>
              </div>
              <div className="col-md-5 card py-3 px-0 rounded">
                <img
                  src={
                    student.profile_img ||
                    "https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"
                  }
                  alt="Admin"
                  className="rounded m-1"
                  width=""
                />
              </div>
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer className="justify-content-center">
          <Button className="px-3" variant="secondary" onClick={handleEdit}>
            Edit
          </Button>
          <Button
            className="px-1"
            variant="secondary"
            onClick={handleShowLogout}
          >
            Log out
          </Button>
        </Modal.Footer>
      </Modal>

      {/* This is Logout confirmation showing modal */}
      <Modal show={showLogout}>
        <Modal.Header>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Are you sure you want to Logout.</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseLogout}>
            Candel
          </Button>
          <Button
            variant="primary"
            onClick={() => {
              handleLogout();
              handleCloseLogout();
            }}
          >
            Logout
          </Button>
        </Modal.Footer>
      </Modal>

      {/* This is side bar */}
      <>
    <div className="border m-3">
      <div className="d-flex justify-content-between m-2">
        <div className="col-md-2 border card my-2 p-1 ">
          <h3 className=" m-0 px-3 b-0 w-100 text-center pt-2">Year</h3>
          <hr />
          <Link to={`/student/2002`}>
            <button
              type="button"
              className="btn btn-secondary w-100 btn-block d-flex justify-content-center my-1 btn-sm"
            >
              2002
            </button>
          </Link>
          <Link to={`/student/2003`}>
            <button
              type="button"
              className="btn btn-secondary w-100 btn-block d-flex justify-content-center mb-1 btn-sm"
            >
              2003
            </button>
          </Link>
          <Link to={`/student/2004`}>
            <button
              type="button"
              className="btn btn-secondary w-100 btn-block d-flex justify-content-center mb-1 btn-sm"
            >
              2004
            </button>
          </Link>
        </div>
        <div className="col-md-9">
          {props.year ? (
            <Student year={props.year} />
          ) : (
            <>
              <div className="row m-4 ">
                <div class="col  b-2 p-3 ">
                  <div className="d-flex justify-content-center text-end">
                    <h3>This Student section of our University</h3>
                  </div>
                  <hr />
                  <p className=" ">
                    <small className="fst-italic ">
                      BUMCA, the Berhampur University MCA Alumni, represents
                      over 600+ alumni members who excelled in the IT industry.
                      Our mission is to enhance bonding among alumni members,
                      connect with students, and foster synergy with teachers.
                      We are committed to our alma-mater by contributing to its
                      academic, research, and off-campus programs. We organize
                      annual fund-raising campaigns to provide scholarships and
                      financial aid to students.
                    </small>
                  </p>
                </div>
                <div
                  id="carouselExampleAutoplaying"
                  class="carousel slide p-4 b-2 col col-7"
                  data-bs-ride="carousel"
                  style={{ height: "350px", overflow: "hidden" }}
                >
                  <div class="carousel-inner">
                    <div class="carousel-item active">
                      <img
                        src={
                          homeContent.img_1 ||
                          "https://www.techfry.com/images/coding/coding05.jpg"
                        }
                        class="d-block w-100"
                        alt="..."
                      />
                    </div>
                    <div class="carousel-item">
                      <img
                        src={
                          homeContent.img_2 ||
                          "https://www.techfry.com/images/coding/coding05.jpg"
                        }
                        class="d-block w-100"
                        alt="..."
                      />
                    </div>
                    <div class="carousel-item">
                      <img
                        src={
                          homeContent.img_3 ||
                          "https://www.techfry.com/images/coding/coding05.jpg"
                        }
                        class="d-block w-100"
                        alt="..."
                      />
                    </div>
                  </div>
                  <button
                    class="carousel-control-prev"
                    type="button"
                    data-bs-target="#carouselExampleAutoplaying"
                    data-bs-slide="prev"
                  >
                    <span
                      class="carousel-control-prev-icon"
                      aria-hidden="true"
                    ></span>
                    <span class="visually-hidden">Previous</span>
                  </button>
                  <button
                    class="carousel-control-next"
                    type="button"
                    data-bs-target="#carouselExampleAutoplaying"
                    data-bs-slide="next"
                  >
                    <span
                      class="carousel-control-next-icon"
                      aria-hidden="true"
                    ></span>
                    <span class="visually-hidden">Next</span>
                  </button>
                </div>
              </div>
            </>
          )}
        </div>
      </div>
      </div>
    </>
      <Outlet />
    </>
  );
};

export default Header;
