import React, { useContext, useEffect, useState } from "react";
import StudentContext from "../contextstate/StudentContext";
// import './component styling/Carousel.js'

const Home = () => {
  const context = useContext(StudentContext);
  const { setShowHeader } = context;
  setShowHeader("");
  const [homeContent, sethomeContent] = useState({});

  const getPageData = async () => {
    try {
      const response = await fetch(
        `http://localhost:4000/aadmin/home/webPageOptions`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      const result = await response.json();
      // console.log(result.result.homeCarousel;
      return result.result[0];
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    getPageData().then((resolve) => {
      sethomeContent({
        homeCarousel: resolve.homeCarousel,
        stafCarousel: resolve.stafData.stafCarousel,
        studentCarousel: resolve.studentCarousel,
      });
    });
    console.log(homeContent);
  }, []);
  return (
    <>
      <div className="row m-4">
        {/* <div class="row justify-content-md-center"> */}
        <div class="col border border-primary b-2 p-3">
            <h3>This is our University</h3>
            <hr />
            <small>This is the official site of BUMCA - the Berhampur University MCA Alumni. BUMCA represents the alumni of the Post Graduates in Computer Application of Berhampur University. The 600+ alumni members placed around the world continue to excel in IT industry. We strive for excellence by updating and adapting ourselves to the changing and growing needs of the society and Students' community. Our motto is to establish better bonding among the alumni members, reach out to the students of the department and develop synergy with our beloved teachers. We are indebted to the University for getting the best of the education, resources and valuable guidance because of which we are, what we are today. We are strongly committed to our alma-mater for its better future by contributing towards development of the institute's academics, research and off-campus programs, expansion and renewal of its facilities and providing scholarships and financial aid to students through annual fund-raising campaigns.

As part of our continuous efforts towards connecting to the university, we are geared towards our second alumni reunion meet "Sangam 2014" at Berhampur University soil on 29th Dec 2014.</small>
        </div>
        <div class="col col-7 border border-primary b-2 p-4">
          <div
            id="carouselExampleAutoplaying"
            class="carousel slide"
            data-bs-ride="carousel"
          >
            <div class="carousel-inner">
              <div class="carousel-item active">
                <img
                  src={
                    homeContent.homeCarousel
                      ? homeContent.homeCarousel.img_1
                      : ""
                  }
                  class="d-block w-100"
                  alt="..."
                />
              </div>
              <div class="carousel-item">
                <img
                  src={
                    homeContent.homeCarousel
                      ? homeContent.homeCarousel.img_2
                      : ""
                  }
                  class="d-block w-100"
                  alt="..."
                />
              </div>
              <div class="carousel-item">
                <img
                  src={
                    homeContent.homeCarousel
                      ? homeContent.homeCarousel.img_3
                      : ""
                  }
                  class="d-block w-100"
                  alt="..."
                />
              </div>
            </div>
            <button
              class="carousel-control-prev"
              type="button"
              data-bs-target="#carouselExampleAutoplaying"
              data-bs-slide="prev"
            >
              <span
                class="carousel-control-prev-icon"
                aria-hidden="true"
              ></span>
              <span class="visually-hidden">Previous</span>
            </button>
            <button
              class="carousel-control-next"
              type="button"
              data-bs-target="#carouselExampleAutoplaying"
              data-bs-slide="next"
            >
              <span
                class="carousel-control-next-icon"
                aria-hidden="true"
              ></span>
              <span class="visually-hidden">Next</span>
            </button>
          </div>
        </div>
        {/* </div> */}
      </div>
      
      <div className="container ">
      </div>
    </>
  );
};

export default Home;
