import React, { useContext, useEffect, useState } from 'react';
import StudentContext from '../../contextstate/StudentContext';
import { useNavigate } from 'react-router-dom';


const SignupFaculty = () => {
  const [credentials, setCredentials] = useState({name:"",email:"",password:""})
    const context = useContext(StudentContext)
    const {stafSignup} = context
    const navigate = useNavigate()

    const onchange = (e)=>{
        setCredentials({...credentials,[e.target.name]:e.target.value})
    }
    const handleSubmit = (e)=>{
        e.preventDefault()
        stafSignup(credentials.name,credentials.email,credentials.password).then((resolve)=>{
            if (resolve) {
                alert("Successfully Sign-up")
                // navigate(`/student/authorised/registration/${localStorage.getItem("token")}`)
                navigate("/")
            }else{
                alert("Something went wrong check Console...")
            }
        })

    }
    useEffect(() => {
      console.log(credentials.name,credentials.email,credentials.password)
    }, [credentials])


    return (<>
    
    <div class="login-box">
  <h2>Sign Up</h2>
  <form 
  onSubmit={handleSubmit}>
    <div class="user-box">
      <input 
      type="name" 
      name="name" 
      placeholder='name'
      value={credentials.name}
      onChange={onchange}
      className='rounded pt-4 px-2' 
      required=""
      />
      <label className='my-3'>User Name</label>
    </div>
    <div class="user-box">
      <input 
      type="email" 
      name="emial" 
      placeholder=''
      value={credentials.email}
      onChange={onchange}
      autoComplete=''
      className='rounded pt-4 px-2' 
      required/>
      <label className='my-3'>Mail Id</label>
    </div>
    <div class="user-box">
      <input 
      type="password" 
      name="password" 
      autoComplete='new-password'
      placeholder='Password'
      value={credentials.password}
      onChange={onchange}
      required
      minLength={4}
      className='rounded pt-4 px-2' />
      <label className='my-3'>Password</label>
    </div>
    <a href="#" className='m-0'>        
      SignUp
    </a>
  </form>
</div>
    </>);
}

export default SignupFaculty;
