import React, { useContext, useState } from "react";
// import { Link } from "react-router-dom";
import "./component styling/Login.css";
import { Link, useNavigate } from "react-router-dom";
import StudentContext from "../contextstate/StudentContext";

const Login = () => {
  const [credentials, setCredentials] = useState({ email: "", password: "" });
  const context = useContext(StudentContext);
  const { studentLogin } = context;
  let navigate = useNavigate();

  const onChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };
  const handleLoginClick = (e) => {
    e.preventDefault();
    studentLogin(credentials.email, credentials.password).then((resolve) => {
      if (resolve) {
        alert("Successfully login");
        navigate("/");
      } else {
        console.log("Something went wrong");
      }
    });
  };

  return (
    <>
      <div className="d-flex justify-content-end mx-3 ">
        <div className="login-form">
          <form onSubmit={handleLoginClick}>
            <h1>Login</h1>
            <div className="content">
              <div className="input-field">
                <input
                  type="email"
                  name="email"
                  placeholder="Email"
                  autoComplete="nope"
                  value={credentials.email}
                  onChange={onChange}
                />
              </div>
              <div className="input-field">
                <input
                  type="password"
                  name="password"
                  placeholder="Password"
                  autoComplete="new-password"
                  value={credentials.password}
                  onChange={onChange}
                />
              </div>
              <Link to="/signup" className="link">
                click here for Sign up
              </Link>
            </div>
            <div className="action">
              {/* <button><Link to="/adminlogin" className='nav-item nav-link'>Admin Log in</Link></button> */}
              <button type="submit" className="active-button ">
                Log in
              </button>
            </div>
          </form>
        </div>
        <div className="row" style={{ position: "absolute" }}>
          <div class="card col border p-1 rounded shadow">
          staf
          <i
            
            className="fa-solid fa-arrow-right"
            style={{
              cursor: "pointer",
              right: "10px",
            }}
          ></i>
          </div>
        </div>
      </div>
    </>
  );
};

export default Login;

